var autopopulatefield = Class.create();
autopopulatefield.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getInfo: function(){
		var obj={};
		obj.firstname='';
		obj.lastname='';
		var id=this.getParameter('sysparm_user_id');
   var gr= new GlideRecord('sys_user');
 gr.addQuery('sys_id',id);
 gr.query();
		if(gr.next()){
			obj.firstname=gr.getDispalyValue('u_firstname');
			obj.lastname=gr.getDispalyValue('u_lastname');
		}
 var json = new JSON();
 var data = json.encode(obj);
 return data;



   },



   // type: 'autopopulatefield'
});
